import pandas as pdpro_df = pd.read_csv("~/Documents/src/karenina/promote", sep="\t",                       names = ['chr', 'start', 'end', 'gene'])mod_df = pd.read_csv("~/Documents/data/karenina/rr3_cut.bedmethyl", sep="\t",                     names = ['chr', 'pos', 'code', 'beta'])merged = pd.merge(mod_df, pro_df, on='chr', how='left')# Then filter rows where pos is between start and endfiltered_merged = merged.query('pos >= start and pos <= end')# Alternatively, if you want to keep the gene information as well:result_df_with_gene = filtered_merged